import React, { Component } from "react";
import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Checkbox,
  ListItemText,
  withStyles
} from "@material-ui/core";

const styles = theme => ({
  formControl: {
    margin: theme.spacing.unit,
    width: 200
  }
});

class SelectInput extends Component {
  selection = [];

  handle_change = name => event => {
    this.setState({ [name]: event.target.value });
  };

  handle_change = event => {
    this.selection.push(event.target.value);
    console.log(this.selection);
  };

  render() {
    const { classes, values, handleChange, data } = this.props;

    return (
      <FormControl className={classes.formControl}>
        <InputLabel htmlFor="select-multiple-checkbox">{data}</InputLabel>
        <Select
          multiple
          value={this.selection}
          onChange={handleChange("discTypeSelector")}
          inputProps={{
            name: data,
            id: `${data}-native-simple`
          }}
        >
          {values.map(value => (
            <MenuItem key={values.indexOf(value)}>
              <Checkbox checked={values.indexOf(value) > -1} />
              <ListItemText name={value} />
            </MenuItem>
          ))}
        </Select>
      </FormControl>
    );
  }
}

export default withStyles(styles)(SelectInput);
