import React, { Component, Fragment } from "react";
import { CssBaseline } from "@material-ui/core";
import { connect } from "react-redux";
import { getDiscs } from "../redux/actions/discsActions";

import { Header, Footer } from "./Layout/index";
import Drawer from "./header-actions/Filter/Drawer/Drawer";
import Discs from "./Discs/Discs";

class App extends Component {
  state = {
    toggleRatings: false,
    add_disc_open: false,
    filter_open: false
  };

  componentDidMount() {
    this.props.getDiscs();
  }

  toggle_ratings = () => {
    this.setState({ infiniteRatings: !this.state.infiniteRatings });
  };

  add_disc_toggle = () => {
    this.setState({
      add_disc_open: !this.state.add_disc_open
    });
  };

  filter_toggle = () => {
    this.setState({
      filter_open: !this.state.filter_open
    });
  };

  render() {
    return (
      <Fragment>
        <CssBaseline />
        <Header filterToggle={this.filter_toggle} />
        <Drawer
          toggle={this.filter_toggle}
          status={this.state.filter_open}
          toggleRatings={this.toggle_ratings}
          toggleRatingsStatus={this.state.toggleRatings}
        />
        <Discs />
      </Fragment>
    );
  }
}

const mapStateToProps = state => ({
  discs: state.discs
});

export default connect(
  mapStateToProps,
  { getDiscs }
)(App);
