import React, { Component, Fragment } from "react";
import { CssBaseline } from "@material-ui/core";
import { Provider } from "react-redux";
import store from "../redux/store";

import { Header, Footer } from "./Layout/index";
import Drawer from "./header-actions/Filter/Drawer/Drawer";
import Discs from "./Discs/Discs";

class App extends Component {
  state = {
    discs: this.props.discs.discs ? this.props.discs.discs : [],
    add_disc_open: false,
    filter_open: false
  };

  componentDidMount() {
    this.props.getDiscs();
  }

  add_disc_toggle = () => {
    this.setState({
      add_disc_open: !this.state.add_disc_open
    });
  };

  filter_toggle = () => {
    this.setState({
      filter_open: !this.state.filter_open
    });
  };

  render() {
    return (
      <Provider store={store}>
        <Fragment>
          <CssBaseline />
          <Header filterToggle={this.filter_toggle} />
          <Drawer toggle={this.filter_toggle} status={this.state.filter_open} />
          <Discs discs={this.state.discs} />
        </Fragment>
      </Provider>
    );
  }
}

export default App;
