import React, { Component, Fragment } from "react";
import { CssBaseline } from "@material-ui/core";
import { connect } from "react-redux";
import { getDiscs } from "../redux/actions/discsActions";

import { Header, Footer } from "./Layout/index";
import Drawer from "./header-actions/Filter/Drawer/Drawer";
import Discs from "./Discs/Discs";

class App extends Component {
  state = {
    toggleRatings: false,
    add_disc_open: false,
    filter_open: false,
    manufactureSelections: [],
    discTypeSelections: []
  };

  componentDidMount() {
    this.props.getDiscs();
  }

  toggle_ratings = () => {
    this.setState({ toggleRatings: !this.state.toggleRatings });
  };

  add_disc_toggle = () => {
    this.setState({
      add_disc_open: !this.state.add_disc_open
    });
  };

  filter_toggle = () => {
    this.setState({
      filter_open: !this.state.filter_open
    });
  };

  filter_handlers = {
    manufacture: e => {
      this.setState({ manufactureSelections: e.target.value });
      console.log(e.target.value);
    },
    type: e => this.setState({ discTypeSelections: e.target.value })
  };

  clear_button = () => {
    this.setState({ manufactureSelections: [], discTypeSelections: [] });
  };

  render() {
    return (
      <Fragment>
        <CssBaseline />
        <Header filterToggle={this.filter_toggle} />
        <Drawer
          filterToggle={this.filter_toggle}
          filterStatus={this.state.filter_open}
          toggleRatings={this.toggle_ratings}
          toggleRatingsStatus={this.state.toggleRatings}
          filterHandlers={this.filter_handlers}
          manufactureSelections={this.state.manufactureSelections}
          discTypeSelections={this.state.discTypeSelections}
          selections={{ manufacture: this.state.manufactureSelections }}
          clearButton={this.clear_button}
        />
        <Discs toggleRatingsStatus={this.state.toggleRatings} />
      </Fragment>
    );
  }
}

const mapStateToProps = state => ({
  discs: state.discs
});

export default connect(
  mapStateToProps,
  { getDiscs }
)(App);
