import React, { Component } from "react";
import { Grid, withStyles } from "@material-ui/core";
import { connect } from "react-redux";
import { getDiscs } from "../../redux/actions/discsActions";

import Disc from "./Disc/Disc";

const styles = {
  container: {
    height: "calc(100% - 64px)",
    marginTop: 64,
    maxWidth: 1200,
    margin: "auto"
  }
};

class Discs extends Component {
  searchByNameHandler = () => {
    let input = document.getElementById("name-search");
    let filter = input.value.toUpperCase();
    let cards = document.querySelectorAll("#disc-card");

    for (let i = 0; i < cards.length; i++) {
      let name = cards[i].firstChild.textContent;
      if (name.toUpperCase().indexOf(filter) > -1) {
        cards[i].style.display = "";
      } else {
        cards[i].style.display = "none";
      }
    }
  };

  render() {
    const { classes } = this.props;
    let { discs } = this.props.discs;

    const shuffledArray = array => {
      let currentIndex = array.length,
        temporaryValue,
        randomIndex;

      while (0 !== currentIndex) {
        randomIndex = Math.floor(Math.random() * currentIndex);
        currentIndex -= 1;

        temporaryValue = array[currentIndex];
        array[currentIndex] = array[randomIndex];
        array[randomIndex] = temporaryValue;
      }

      return array;
    };

    let content;

    if (Object.keys(discs).length > 0) {
      discs = shuffledArray(discs);
      content = discs.map(disc => (
        <Grid item key={discs.indexOf(disc)}>
          <Disc data={disc} />
        </Grid>
      ));
    }
    return (
      <Grid
        container
        justify="center"
        spacing={32}
        className={classes.container}
      >
        {content}
      </Grid>
    );
  }
}

// Discs.propTypes = {};

const mapStateToProps = state => ({
  discs: state.discs
});

export default connect(
  mapStateToProps,
  { getDiscs }
)(withStyles(styles)(Discs));
