import React, { Component } from "react";
import {
  List,
  ListItem,
  TextField,
  Button,
  FormGroup,
  FormControlLabel,
  Switch,
  withStyles
} from "@material-ui/core";
import SelectInput from "./Select/SelectInput";

import selectValues from "./selectValues";

const styles = theme => ({
  input: {
    margin: theme.spacing.unit
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200
  },
  button: {
    width: 75,

    margin: "auto"
  }
});

class ListComponent extends Component {
  searchByNameHandler = () => {
    let input = document.getElementById("name-search");
    let filter = input.value.toUpperCase();
    let cards = document.querySelectorAll("#disc-card");

    for (let i = 0; i < cards.length; i++) {
      let name =
        cards[i].lastChild.firstChild.firstChild.firstChild.textContent;
      if (name.toUpperCase().indexOf(filter) > -1) {
        cards[i].style.display = "";
      } else {
        cards[i].style.display = "none";
      }
    }
  };

  render() {
    const {
      classes,
      toggleRatings,
      toggleRatingsStatus,
      handleChange,
      discTypeSelector,
      manufactureSelector
    } = this.props;

    return (
      <List component="ul">
        <ListItem>
          <FormGroup row>
            <FormControlLabel
              control={
                <Switch
                  checked={toggleRatingsStatus}
                  onChange={toggleRatings}
                  value="checkedA"
                />
              }
              label={
                toggleRatingsStatus === false
                  ? "Infinite Ratings"
                  : "Manufacture Ratings"
              }
            />
          </FormGroup>
        </ListItem>
        <ListItem>
          <TextField
            className={classes.textField}
            label="Search by name"
            id="name-search"
            onChange={this.searchByNameHandler}
          />
        </ListItem>
        <ListItem>
          <SelectInput
            values={selectValues.manufacture}
            handleChange={handleChange}
            selection={manufactureSelector}
            data="manufacture"
          />
        </ListItem>
        <ListItem>
          <SelectInput
            values={selectValues.discTypes}
            handleChange={handleChange}
            selection={discTypeSelector}
            data="type"
          />
        </ListItem>
        <ListItem style={{ width: "90%", margin: "auto" }}>
          <Button variant="contained" className={classes.button}>
            Submit
          </Button>
          <Button variant="contained" className={classes.button}>
            Reset
          </Button>
        </ListItem>
      </List>
    );
  }
}

export default withStyles(styles)(ListComponent);
