import React, { Component } from "react";
import { Grid, withStyles } from "@material-ui/core";
import { connect } from "react-redux";
import { getDiscs } from "../../redux/actions/discsActions";

import Disc from "./Disc/Disc";

const styles = {
  container: {
    height: "calc(100% - 64px)",
    marginTop: 64,
    maxWidth: 1200,
    margin: "auto"
  }
};

class Discs extends Component {
  render() {
    const { classes, discs } = this.props;

    let content;
    console.log(discs);
    if (Object.keys(discs).length > 0) {
      content = discs.map(disc => (
        <Grid item key={discs.indexOf(disc)}>
          <Disc data={disc} />
        </Grid>
      ));
    }
    return (
      <Grid container justify="center" className={classes.container}>
        {content}
      </Grid>
    );
  }
}

// Discs.propTypes = {};

const mapStateToProps = state => ({
  discs: state.discs
});

export default connect(
  mapStateToProps,
  { getDiscs }
)(withStyles(styles)(Discs));
