import React, { Component, Fragment } from "react";
import { CssBaseline } from "@material-ui/core";
import { connect } from "react-redux";
import { getDiscs } from "../redux/actions/discsActions";

import { Header, Footer } from "./Layout/index";
import Drawer from "./header-actions/Filter/Drawer/Drawer";
import Discs from "./Discs/Discs";

class App extends Component {
  state = {
    toggleRatings: false,
    add_disc_open: false,
    filter_open: false,
    discTypeSelector: "",
    manufactureSelector: ""
  };

  componentDidMount() {
    this.props.getDiscs();
  }

  toggle_ratings = () => {
    this.setState({ toggleRatings: !this.state.toggleRatings });
  };

  add_disc_toggle = () => {
    this.setState({
      add_disc_open: !this.state.add_disc_open
    });
  };

  filter_toggle = () => {
    this.setState({
      filter_open: !this.state.filter_open
    });
  };

  handle_change = name => event => {
    this.setState({ [name]: event.target.value });
  };

  reset_button = () => {
    this.setState({
      discTypeSelector: "Hello",
      manufactureSelector: ""
    });
    console.log(this.state.manufactureSelector);
  };

  render() {
    return (
      <Fragment>
        <CssBaseline />
        <Header filterToggle={this.filter_toggle} />
        <Drawer
          handleChange={this.handle_change}
          toggle={this.filter_toggle}
          status={this.state.filter_open}
          toggleRatings={this.toggle_ratings}
          toggleRatingsStatus={this.state.toggleRatings}
          discTypeSelector={this.state.discTypeSelector}
          manufactureSelector={this.state.manufacture}
          reset={this.reset_button}
        />
        <Discs toggleRatingsStatus={this.state.toggleRatings} />
      </Fragment>
    );
  }
}

const mapStateToProps = state => ({
  discs: state.discs
});

export default connect(
  mapStateToProps,
  { getDiscs }
)(App);
