import React, { Component, Fragment } from "react";
import { CssBaseline } from "@material-ui/core";
import { connect } from "react-redux";
import { getDiscs } from "../redux/actions/discsActions";

import { Header, Footer } from "./Layout/index";
import Drawer from "./header-actions/Filter/Drawer/Drawer";
import Discs from "./Discs/Discs";

class App extends Component {
  state = {
    toggleRatings: false,
    add_disc_open: false,
    filter_open: false,
    manufactureSelections: [],
    discTypeSelections: []
  };

  componentDidMount() {
    this.props.getDiscs();
  }

  toggle_ratings = () => {
    this.setState({ toggleRatings: !this.state.toggleRatings });
  };

  add_disc_toggle = () => {
    this.setState({
      add_disc_open: !this.state.add_disc_open
    });
  };

  filter_toggle = () => {
    this.setState({
      filter_open: !this.state.filter_open
    });
  };

  filter_handlers = {
    manufacture: e => this.setState({ manufactureSelection: e.target.value }),

    type: e => this.setState({ discTypeSelection: e.target.value })
  };

  filter_selections = {
    manufacture: this.state.manufactureSelections,
    type: this.state.discTypeSelections
  };

  reset_button = () => {
    console.log(document.getElementById("Type-native-simple"));
    document.getElementById("Type-native-simple").value = "";
    document.getElementById("Manufacture-native-simple").value = "";
  };

  render() {
    return (
      <Fragment>
        <CssBaseline />
        <Header filterToggle={this.filter_toggle} />
        <Drawer
          filterToggle={this.filter_toggle}
          filterStatus={this.state.filter_open}
          toggleRatings={this.toggle_ratings}
          toggleRatingsStatus={this.state.toggleRatings}
          filterHandlers={this.filter_handlers}
          filterSelections={this.filter_selections}
          resetButton={this.reset_button}
        />
        <Discs toggleRatingsStatus={this.state.toggleRatings} />
      </Fragment>
    );
  }
}

const mapStateToProps = state => ({
  discs: state.discs
});

export default connect(
  mapStateToProps,
  { getDiscs }
)(App);
